import React from "react";

const NERText = ({ data }) => {
    // ฟังก์ชันสำหรับรวม token ย่อย (##) ให้เป็นคำเดียว
    const mergeTokens = (tokens) => {
        const merged = [];
        let currentToken = "";
        let currentLabel = "";
        let currentConfidence = 0;

        tokens.forEach((t, index) => {
        // ดึง label ที่ confidence สูงสุดจาก all_probs
        const highestLabel = Object.entries(t.all_probs).reduce(
            (max, [label, prob]) => prob > max.prob ? { label, prob } : max,
            { label: "", prob: 0 }
        );

        // ข้าม label ที่เป็น "O"
        if (highestLabel.label === "O") return;

        if (t.token.startsWith("##")) {
            currentToken += t.token.replace("##", "");
        } else {
            if (currentToken) {
                // push token ก่อนหน้า
                merged.push({
                    token: currentToken,
                    label: currentLabel,
                    confidence: currentConfidence,
                });
            }
            // เริ่ม token ใหม่
            currentToken = t.token;
            currentLabel = highestLabel.label;
            currentConfidence = highestLabel.prob;
        }

        // กรณี token สุดท้าย
        if (index === tokens.length - 1) {
            if (currentToken) {
            merged.push({
                token: currentToken,
                label: currentLabel,
                confidence: currentConfidence,
            });
            }
        }
    });

    return merged;
};

const mergedTokens = mergeTokens(data);

//   const textWithEntities = mergedTokens.map((t, idx) => {
//     return `${t.token} [${t.label}] `;
//   }).join("");

return (
    <div>
      <h2>ข้อความพร้อม Entities</h2>
      {/* <p>{textWithEntities}</p> */}
      {mergedTokens.length > 0 && (
        <div>
          <h3>Entities Detected:</h3>
          <ul>
            {mergedTokens.map((t, idx) => (
              <span key={idx} className={`entity ${t.label}`}>
                {t.token} - {t.label} (Confidence: {t.confidence.toFixed(2)})
              </span>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default NERText;
