import React, { useState, useEffect } from 'react';
// Firebase configuration
import { db } from './config/firebaseConfig';
import { collection, addDoc, getDocs, deleteDoc, doc, serverTimestamp } from 'firebase/firestore';

function ServReqLists() {
    const [items, setItems] = useState([]);

    const fetchItems = async () => {
      const snapshot = await getDocs(collection(db, "ServiceRequest"));
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      alert(data.doc + " รายการคำร้องขอบริการในระบบ");
      if (data.length === 0) {
        alert("ไม่พบข้อมูลคำร้องขอบริการในระบบ!");
        return;
      }
      setItems(data);
    };
    
    const deleteItem = async (id) => {
      alert("กำลังลบคำร้องขอบริการที่มี ID: " + id);
      if (!id) {
        alert("ไม่พบ ID ของคำร้องขอบริการที่ต้องการลบ!");  
        return;
      }
      if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบคำร้องขอบริการนี้?")) {
        return;
      }
      await deleteDoc(doc(db, "ServiceRequest", id));
      fetchItems();
    };

    useEffect(() => {
      fetchItems();
    }, []);

    return (
    <>
        <div style={{ margin: "5px", padding: "5px", textAlign: "left", fontSize: "1.2rem", padding: "1rem", borderLeft: "10px solid #40c6ed", borderBottom: "1px solid #40c6ed" }}>
            <i className="fa fa-list-ol" style={{ color: "#40c6ed" }}></i>
            <span style={{ marginLeft: "5px", fontWeight: "bold" }}>รายการข้อมูลคำร้อง</span>
        </div>
        <div style={{ padding: "1rem" }}>
            <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "1rem", marginBottom: "2rem" }}>
                <thead>
                    <tr>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>รหัสคำร้อง</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>รายการคำร้อง</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>ความสำคัญ</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>สถานะคำร้อง</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>ดำเนินการ</th>
                    </tr>
                </thead>
                {items.map((item, idx) => {
                    <tr key={idx}>
                        <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", alignContent: "center" }}>{item.SrId}</td>
                        <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", alignContent: "left" }}>{item.SrDescription}</td>
                        <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", alignContent: "center" }}><span className={`entity ${item.SrPriority}`}>{item.SrPriority}</span></td>
                        <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", alignContent: "center" }}>{item.SrStatus}</td>
                        <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", alignContent: "center" }}>
                            <button onClick={() => deleteItem(item.id)} style={{ marginTop: "1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#40c6ed", color: "#fff", border: "none", borderRadius: "4px" }}>รายละเอียด</button>
                            <button onClick={() => deleteItem(item.id)} style={{ marginTop: "1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#40c6ed", color: "#fff", border: "none", borderRadius: "4px" }}>//</button>
                            <button onClick={() => deleteItem(item.id)} style={{ marginTop: "1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#40c6ed", color: "#fff", border: "none", borderRadius: "4px" }}>❌</button>
                        </td>
                    </tr>
                })}
                </table>
        </div>
    </>
    );
}

export default ServReqLists;
