import React from "react";

const NERFilterByConfidence = ({ data }) => {
  // Filter token & label ที่มี confidence score สูงสุด
  const filteredTokens = data.map((item) => {
    // หาค่า label ที่ confidence สูงสุดจาก all_probs
    const highestLabel = Object.entries(item.all_probs).reduce(
      (max, [label, prob]) => prob > max.prob ? { label, prob } : max,
      { label: "", prob: 0 }
    );

    return {
      token: item.token,
      label: highestLabel.label,
      confidence: highestLabel.prob,
    };
  });

  return (
    <div>
      <h3>ผลลัพธ์ (Filtered by Highest Confidence)</h3>
      <ul>
        {filteredTokens.map((t, index) => (
          <li key={index}>
            <strong>{t.token}</strong> - {t.label} (Confidence: {t.confidence.toFixed(2)})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NERFilterByConfidence;
