import React, { useState } from "react";
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [entities, setEntities] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:8000/ner", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text: inputText }),
    });
    const data = await response.json();
    setEntities(data.entities);
  };

  const highlightText = () => {
    if (entities.length === 0) return inputText;

    // Tokenize original text (simple split by space for demo)
    const words = inputText.split(" ");
    return words.map((word, index) => {
      const entity = entities.find((ent) => ent.token === word);
      if (entity) {
        return (
          <span key={index} className={`entity ${entity.label}`}>
            {word + " [" + entity.label + "] "}
          </span>
        );
      } else {
        return word + " ";
      }
    });
  };

  return (
    <div className="App">
      <h1>Named Entity Recognition</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          rows="4"
          cols="50"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Enter text here..."
        />
        <br />
        <button type="submit">Analyze</button>
      </form>
      <h2>Entities</h2>
      <div className="result">
        {highlightText()}
        <br/>
        {entities.JSON.stringify(entities, null, 2)}
      </div>
    </div>
  );
}

export default App;
