import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import "./App.css";
import NERText from "./NERText";
import ServReqLists from "./ServReqLists";

function App() {
  const [inputText, setInputText] = useState("");
  const [entities, setEntities] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch("http://localhost:8000/ner", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ text: inputText }),
      });
      const data = await response.json();
      setEntities(data.entities);
    } catch (error) {
      setEntities([]);
    } finally {
      setLoading(false);
    }
  };

  const highlightText = () => {
    if (entities.length === 0) return inputText;

    // Tokenize original text (simple split by space for demo)
    const words = inputText.split(" ");
    return words.map((word, index) => {
      const entity = entities.find((ent) => ent.token === word);
      if (entity) {
        return (
          <span key={index} className={`entity ${entity.label}`}>
            {word + " [" + entity.label + "] "}
          </span>
        );
      } else {
        return word + " ";
      }
    });
  };

  return (
    <>
    <div style={{ margin: "0px", textAlign: "center", fontSize: "1.4rem", padding: "1rem", backgroundColor: "#40c6ed" }}>
      <i class="fa fa-commenting"></i> 
      <span style={{ marginLeft: "5px", fontWeight: "bold" }}>Automated Prioritization for Monitoring and Tracking Service Requests</span>
    </div>
    <ul>
      <li><a href="/">หน้าแรก</a></li>
      <li><a href="/">กรอกข้อมูลคำร้อง</a></li>
      <li><a href="/ServReqLists">รายการข้อมูลคำร้อง</a></li>
      <li><a href="#contact">ข้อมูลติดต่อ</a></li>
      <li><a href="#about">เกี่ยวกับงานวิจัย</a></li>
    </ul>

    <Router>
      <nav>
        {/* Navigation Menu */}
        <Link to="/">Home</Link> | <Link to="/ServReqLists">ServReqLists</Link>
      </nav>

    </Router>

    <div style={{ maxWidth: "700px", margin: "auto", padding: "2rem" }}>
      <form onSubmit={handleSubmit}>
        <label htmlFor="text" style={{ fontSize: "1.2rem", marginBottom: "0.5rem" }}>
          <i class="fa fa-commenting" style={{ color: "#40c6ed" }}></i>
          <span style={{ marginLeft: "5px", fontWeight: "bold" }}> คำร้องขอบริการ (Service Request)</span>
        </label>
        <br />
        <span style={{ fontSize: "0.8rem", color: "#888" }}>
          <i class="fa fa-info-circle" style={{ color: "#40c6ed" }}></i>
          <span style={{ marginLeft: "5px" }}> กรุณากรอกคำร้องขอบริการที่ต้องการวิเคราะห์</span>
        </span>
        <br />
        <textarea
          rows="4"
          cols="50"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="ระบุข้อมูลคำร้องขอบริการ..."
          style={{ width: "100%", padding: "1rem", fontSize: "1rem" }}
          required
        />
        <br />
        <div style={{ alignItems: "center", color: "#555", display: "flex", justifyContent: "center", alignItems: "center" }}>
          <button type="submit" disabled={loading} style={{ marginTop: "1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#40c6ed", color: "#fff", border: "none", borderRadius: "4px" }}>
            {loading ? "กำลังวิเคราะห์ข้อมูล..." : "วิเคราะห์คำร้องขอบริการ (Analyze Service Request)"}
          </button>
        </div>
      </form>

      {entities.length > 0 && (
        <div style={{ marginTop: "2rem" }}>
          <label>
            <i class="fa fa-search" style={{ color: "#40c6ed" }}></i>
            <span style={{ marginLeft: "5px", fontWeight: "bold" }}> การวิเคราะห์คำร้องขอบริการ (Service Request Analysis)</span>
          </label>
          <NERText data={entities} />
        </div>
      )}

      <div style={{ textAlign: "center", fontSize: "0.8rem", marginTop: "2rem", borderTop: "1px solid #ccc", paddingTop: "1rem" }}>
        <span>© 2025 Chumpol Mokarat, Pattarak Sawatdee, and Pimpika Intusing</span>
        <br />
        <span>Version: 1.0</span>
        <br />
        <span>Contact: <a href="mailto:chumpol_mo@rmutto.ac.th">chumpol_mo@rmutto.ac.th</a></span>
      </div>
    </div>
    </>
  );
}

export default App;
