import React from "react";

const NERText = ({ data }) => {
  // ฟังก์ชันรวม token ย่อย (##) และเลือก label ที่มั่นใจที่สุด (ไม่รวม O)
  const mergeTokens = (tokens) => {
    const merged = [];
    let currentToken = "";
    let currentLabel = "";
    let currentConfidence = 0;

    tokens.forEach((t, index) => {
      // หา label ที่มั่นใจที่สุดจาก all_probs
      const probs = t.all_probs;
      const filteredProbs = Object.entries(probs).filter(([label]) => label !== "O");
      const [bestLabel, bestConfidence] = filteredProbs.reduce((max, curr) => curr[1] > max[1] ? curr : max, ["", 0]);

      if (t.token.startsWith("##")) {
        currentToken += t.token.replace("##", "");
      } else {
        if (currentToken) {
          if (currentLabel && currentLabel !== "O") {
            merged.push({
              token: currentToken,
              label: currentLabel,
              confidence: currentConfidence,
            });
          }
        }
        currentToken = t.token;
        currentLabel = bestLabel;
        currentConfidence = bestConfidence;
      }

      if (index === tokens.length - 1) {
        if (currentToken && currentLabel && currentLabel !== "O") {
          merged.push({
            token: currentToken,
            label: currentLabel,
            confidence: currentConfidence,
          });
        }
      }
    });

    return merged;
  };

  const mergedTokens = mergeTokens(data);

  const textWithEntities = mergedTokens.map((t, idx) => {
    return `${t.token} [${t.label}] `;
  }).join("");

  return (
    <div>
      <h2>ข้อความพร้อม Entities</h2>
      {/* <p>{textWithEntities}</p> */}
      {mergedTokens.length > 0 && (
        <div>
          <h3>Entities Detected:</h3>
          <ul>
            {mergedTokens.map((t, idx) => (
              <span key={idx} className={`entity ${t.label}`}>
                {t.token} - {t.label} (Confidence: {t.confidence.toFixed(2)})
              </span>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default NERText;
