import React, { useState, useEffect } from "react";
// Firebase configuration
import { db } from './config/firebaseConfig';
import { collection, addDoc, getDocs, deleteDoc, doc, serverTimestamp } from 'firebase/firestore';

const NERText = ({ data, formInput }) => {
  alert("NERText component initialized with data: " + JSON.stringify(data) + " and formInput: " + formInput);

    const [inputText, setInputText] = useState(formInput);
    const [text, setText] = useState(
      {
        "SrCreated" : null | "0000-00-00 00:00:00",
        "SrCreatedBy" : "",
        "SrDescription" : "",
        "SrId" : "",
        "SrNote" : "",
        "SrPriority" : "",
        "SrStatus" : 10,
        "SrUpdated" : null | "0000-00-00 00:00:00",
        "SrUpdatedBy" : ""
