import React, { useState, useEffect } from 'react';
// Firebase configuration
import { db } from './config/firebaseConfig';
import { collection, addDoc, getDocs, deleteDoc, doc, serverTimestamp } from 'firebase/firestore';
import ReactPaginate from 'react-paginate';

function ServReqLists() {
    const [items, setItems] = useState([]);
    const [currentPage, setCurrentPage] = useState(0);
    const [totalItems, setTotalItems] = useState(0);
    const itemsPerPage = 10;

    const handlePageChange = (selectedObject) => {
        alert("กำลังเปลี่ยนหน้า: " + selectedObject.selected);
        setCurrentPage(selectedObject.selected);
        setItems(data.slice[selectedObject.selected * itemsPerPage, (selectedObject.selected + 1) * itemsPerPage]);
        fetchItems();
    };

    const fetchItems = async () => {
      const snapshot = await getDocs(collection(db, "ServiceRequest"));
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setTotalItems(data.length); // Example total
      setItems(data);
    };
    
    const deleteItem = async (id) => {
      alert("กำลังลบคำร้องขอบริการที่มี ID: " + id);
      if (!id) {
        alert("ไม่พบ ID ของคำร้องขอบริการที่ต้องการลบ!");  
        return;
      }
      if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบคำร้องขอบริการนี้?")) {
        return;
      }
      await deleteDoc(doc(db, "ServiceRequest", id));
      fetchItems();
    };

    useEffect(() => {
        let isMounted = true;

        if (isMounted) {
            fetchItems();
        }

        return () => {
            isMounted = false;
        };
    }, [items]);

    const data = Array.from({ length: items.length }, (_, i) => `Item ${i + 1}`);

    return (
    <>
        <div style={{ margin: "5px", padding: "5px", textAlign: "left", fontSize: "1.2rem", padding: "1rem", borderLeft: "10px solid #40c6ed", borderBottom: "1px solid #40c6ed" }}>
            <i className="fa fa-list-ol" style={{ color: "#40c6ed" }}></i>
            <span style={{ marginLeft: "5px", fontWeight: "bold" }}>รายการข้อมูลคำร้อง</span>
        </div>
        <div style={{ padding: "1rem" }}>
            <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "1rem", marginBottom: "0.5rem" }}>
                <thead>
                    <tr style={{ backgroundColor: "#f2f2f2" }}>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>รหัสคำร้อง</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>รายการคำร้อง</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>ความสำคัญ</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>สถานะคำร้อง</th>
                    <th style={{ borderCollapse: "collapse", borderTop:"2px solid #CCC", borderBottom:"2px solid #CCC", padding: "3px" }}>ดำเนินการ</th>
                    </tr>
                </thead>
                <tbody>
                {
                    items.length === 0 && (
                        <tr>
                            <td colSpan="5" style={{ textAlign: "center", padding: "1rem", color: "#888" }}>*** ไม่มีข้อมูลคำร้องขอบริการ ***</td>
                        </tr>
                    )
                }
                {
                    items.map((item, idx) => (
                        <tr key={idx}>
                            <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", textAlign: "center" }}>{item.SrId}</td>
                            <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", textAlign: "left" }}>{item.SrDescription}</td>
                            <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", textAlign: "center" }}><span className={`entity ${item.SrPriority}`}>{item.SrPriority}</span></td>
                            <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", textAlign: "center" }}>{item.SrStatus}</td>
                            <td style={{ borderCollapse: "collapse", borderBottom:"1px solid #CCC", padding: "3px", textAlign: "center", margin: "auto" }}>    
                                <button onClick={() => deleteItem(item.id)} style={{ margin: "0.1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#00e6ac", border: "none", borderRadius: "4px" }}>รายละเอียด</button>
                                <button onClick={() => deleteItem(item.id)} style={{ margin: "0.1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#ffc34d", border: "none", borderRadius: "4px" }}>แก้ไข</button>
                                <button onClick={() => deleteItem(item.id)} style={{ margin: "0.1rem", alignItems: "center", padding: "0.5rem", fontSize: "1rem", backgroundColor: "#ff4d4d", border: "none", borderRadius: "4px" }}>ลบ</button>
                            </td>
                        </tr>
                    ))
                }
                </tbody>
                </table>
                <ReactPaginate
                    previousLabel={"ก่อนหน้า"}
                    nextLabel={"ถัดไป"}
                    breakLabel={"..."}
                    pageCount={Math.ceil(totalItems / itemsPerPage)}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={handlePageChange}
                    containerClassName={"pagination"}
                    activeClassName={"active"}
                    pageClassName={"page-item"}
                    pageLinkClassName={"page-link"}
                    previousClassName={"page-item"}
                    previousLinkClassName={"page-link"}
                    nextClassName={"page-item"}
                    nextLinkClassName={"page-link"}
                    breakClassName={"page-item"}
                    breakLinkClassName={"page-link"}
                    disabledClassName={"disabled"}
                />
                <span style={{ fontSize: "0.8rem", color: "#888" }}>
                <i class="fa fa-info-circle" style={{ color: "#40c6ed" }}></i>
                <span style={{ marginLeft: "5px" }}> หมายเหตุ: 10 - เปิดคำร้อง, 20 - รับคำร้อง, 30 - ไม่รับคำร้อง, 40 - กำลังดำเนินการ และ 50 - ปิดคำร้อง</span>
                </span>
        </div>
    </>
    );
}

export default ServReqLists;
